Scripts.OnValueChanged:
local value = self:GetValue()
local valueStep = self:GetValueStep()
local newValue = Poisoner_roundToNextX(value,0,valueStep)
self:SetValue(newValue)
getglobal(self:GetName().."Text"):SetText(newValue);
Poisoner_WarningThreshold = newValue;
POISONER_CONFIG.Timer.WarningThreshold = Poisoner_WarningThreshold;

Scripts.PostClick:
if self:GetChecked() then
	PoisonerOptions_CallUpdate();
end
PoisonerOptions_SaveSettings();

